//bool needRescheduling;
int current;

int get_job()
{
	meta int res = -1;
	for (i : int[0,MAX_TASKS_NUM]) {
        if (is_ready[i]) {
            if (res == -1 || (abs_deadline[i]<abs_deadline[res]))
                res = i;
        }
        if (i == data.numOfTasks-1)
            return res;
    }

    if (res == -1)
        return res;

    for (i : int[0,MAX_TASKS_NUM]) {
        if (is_ready[i]) {
            if ((abs_deadline[i]==abs_deadline[res]) && (id[i]<id[res]))
                res = i;
        }
        if (i == data.numOfTasks-1)
            return res;
    }    

	return res;
}

